import React from 'react';
import { useForm } from 'react-hook-form';
import './Input.css';
import Media from 'react-media';
import { RecaptchaVerifier, signInWithPhoneNumber } from "firebase/auth";
import { auth } from '../firebase';

export let phoneX = '';
let regular = /[a-z]+/;

export default function ReturnSign() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='form-input-signphone'>
  <ReturnInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='form-input-signphone-sm'>
  <ReturnInput />
 </div>
);

export const ReturnInput = () => {

 const generateRecaptcha = () => {
  window.recaptchaVerifier = new RecaptchaVerifier('sign-in', {
   'size': 'invisible',
   'callback': (response) => {
    window.console.log(response);
   }
  }, auth);

 };

 const { register, handleSubmit, reset } = useForm({ defaultValues: { phone: '+243' } });
 const onSubmit = async (data) => {

  if (data.phone.length != 13 || regular.test(data.phone)) {
   // setOpen(true);
   reset();
  }
  else {

   generateRecaptcha();
   const appVerifier = window.recaptchaVerifier;

   signInWithPhoneNumber(auth, data.phone, appVerifier)
    .then((confirmationResult) => {
     // SMS sent. Prompt user to type the code from the message, then sign the
     // user in with confirmationResult.confirm(code).
     window.confirmationResult = confirmationResult;
     // ...
    }).catch((error) => {
     // Error; SMS not sent
     window.console.log(error);
    });


  };
 };
 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text' name='phone' {...register('phone', { required: true })} />
   <div className='sign-in'></div>
   <button className='Btn'>Suivant</button>
  </form>
 )
}
