import React from 'react';
import { HiLockClosed } from 'react-icons/hi';
import { useNavigate } from 'react-router-dom';
import { db } from '../firebase';
import { phoneX } from '../Sign/Input';
import { collection, getDocs, doc, getDoc } from "firebase/firestore";


// Button blocked
export default function ReturnBtnBlocKed() {


};


export const Btn = () => {

 let pushDocs = new Array();
 const [state, setState] = React.useState();

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  navigation('/send-blocked');
 };

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });
  const verifier = pushDocs.some(value => value == phoneX);

  const docRef = doc(db, verifier ? "client" : "agent", phoneX);
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setState(docSnap.data().state)
  }

 }, []);

 return (
  <button onClick={handlepath} className={'Btn-Closed'}>
   <span>
    {state == "client" ? "Retrait" : "Envoyer"}
   </span>
   <div><HiLockClosed size={'1em'} /></div>
  </button>
 );
};
