import React from 'react';
import './IconSuccess.css';
import { VscCheck } from 'react-icons/vsc';
import { TweenMax, Power3 } from 'gsap';
import Media from 'react-media';

// Return Icon Success 
export default function ReturnIconSuccess() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-icon-success'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-icon-success-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => {

 React.useEffect(() => {
  TweenMax.from('.AnimaIcon', 1, { delay: 2, opacity: 0, y: 20, scale: 1, ease: Power3.easeIn });
  TweenMax.from('.AnimaMsg', 1, { delay: 1.2, opacity: 0, y: 20, scale: 1, ease: Power3.easeIn });
 }, []);

 return (
  <>
   <div className='AnimaIcon'>
    <VscCheck size={'2.5em'} />
   </div>
   <p className='AnimaMsg'>Enregistre avec success</p>
  </>
 );
};
