import React from 'react';
import './Avatar.css';
import Media from 'react-media';
import ReturnName from './Name';
import { collection, getDocs, doc, onSnapshot } from "firebase/firestore";
import { db } from '../../../firebase';
import { phoneX } from '../../../Sign/Input';


// Avatar IMAGE VIew
export default function ReturnAvataR() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => {
 return (
  <div className='wrp-avatar-profil-dash'>
   <div className='avatar-circle-profil-dash'>
    <FirstLetteR />
   </div>
   <ReturnName />
  </div>
 );
}
export const ScreenSmall = () => (
 <div className='wrp-avatar-profil-dash-sm'>
  <div className='avatar-circle-profil-dash-sm'>
   <FirstLetteR />
  </div>
  <ReturnName />
 </div>
);
export const FirstLetteR = () => {

 let arrayClient = new Array();
 const [letter, setLetter] = React.useState('M');

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   arrayClient.push(doc.id);
  });

  const collections = arrayClient.some(value => value == phoneX);
  const unsub = onSnapshot(doc(db, collections ? "client" : "agent", phoneX), (doc) => {
   setLetter(doc.data().firstname);
  });

 }, []);

 return (
  <div className='wrp-firstchar-avatar'>
   <h1>{letter[0].toUpperCase()}</h1>
  </div>
 );
}
