import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};


export const SreenLarge = () => {



 return (
  <div className='wrp-input-phone-register'>
   <FormInput />

  </div>
 );
};
export const ScreenSmall = () => {

 return (
  <div className='wrp-input-phone-register-sm'>


   <FormInput />


  </div>
 )
};


export const FormInput = () => {

 const { register, handleSubmit, setValue } = useForm({ defaultValues: { phone: '+243' } })
 const navigation = useNavigate();

 const handleClick = (event) => {
  event.preventDefault();
  setValue('phone', '+243')
  navigation('/register-signin');
 };


 return (
  <form onSubmit={handleSubmit((data) => console.log('submitted', data))}>
   <input type='text' placeholder='Phone' name='phone' {...register('phone')} />

   <div className='wrp-btn'>
    <button onClick={handleClick}>Continue</button>
   </div>

  </form>
 )
}
