import React from 'react';
import './Name.css';
import Media from 'react-media';
import { doc, collection, getDocs, getDoc } from "firebase/firestore";
import { db } from '../../firebase';

let pushArray = new Array();


// Phone Component 
export default function RetuRnNAme() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-name-user-client'>
  <VieWName />
 </div>
)
export const ScreenSmall = () => (
 <div className='wrp-name-user-client-sm'>
  <VieWName />
 </div>
)
export const VieWName = () => {

 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.id);
  });

  const verifier = pushArray.some(value => value == JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE')));
  const docRef = doc(db, verifier ? "client" : "agent", JSON.parse(window.localStorage.getItem('PHONE_USER_REMOVE')));

  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setFirst(docSnap.data().firstname);
   setLast(docSnap.data().lastname);
  } else {
   // doc.data() will be undefined in this case
   console.log("No such document!");
  }

 }, []);


 let user = `${first} ${last}`;

 return (
  <h1>{user.toUpperCase()}</h1>
 )
}
