import React from 'react';
import './Prix.css';
import { TweenLite } from 'gsap';
import { newExchAnge } from '../ValidExUsd/Values';
import Media from 'react-media';

// Prix View Component 
export default function ReturnPriX() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-prix-exchange'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-prix-exchange-sm'>
  <ViewText />
 </div>
);

export const ViewText = () => {

 const [withdraw, setWithdraw] = React.useState(newExchAnge);
 const obj = { value: 0 };

 React.useEffect(() => {

  let target = document.querySelector('.valueTarget');
  TweenLite.to(obj, 1, {
   value: Math.floor(newExchAnge),

   roundProps: { value: 1 },
   onUpdate: () => target.innerHTML = obj.value
  })
  console.log(withdraw);
 }, []);

 return (
  <div className='wrp-prix-exchange'>
   <span>USD</span>
   <p className='valueTarget'></p>
  </div>
 );
};
