import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore";
import { db } from '../firebase';
import FadeLoader from 'react-spinners/FadeLoader';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import REturnQuOte from './Quote';

import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';


// Firebase auth 
export var phoneX = '';
export var state = '';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='input-phone-register'>
  <REturnQuOte />
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='input-phone-register'>
  <REturnQuOte />
  <FormInput />
 </div>
);
export const FormInput = () => {

 let clientDocs = new Array();
 let agentDocs = new Array();

 let regular = /[a-z]+/;
 const [load, setLoad] = React.useState(false);

 const navigation = useNavigate();
 const { register, handleSubmit, reset, control } = useForm({ defaultValues: { phone: '+243' } });

 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClose = () => {
  setOpen(false);
 };

 const onSubmit = async (data) => {
  setLoad(true);

  if (data.phone.length != 13 || regular.test(data.phone)) {

   window.setTimeout(() => {
    setLoad(false);
    setOpen(true);
    reset()
   }, 500);

  } else {

   const clientSnapshot = await getDocs(collection(db, "client"));
   clientSnapshot.forEach((doc) => {
    clientDocs.push(doc.id);
   });

   const agentSnapshot = await getDocs(collection(db, "agent"));
   agentSnapshot.forEach((doc) => {
    agentDocs.push(doc.id);
   });

   const isPhoneInWithDocsClient = clientDocs.some(value => value == data.phone);
   const isPhoneInWithDocsAgent = agentDocs.some(value => value == data.phone);

   if (isPhoneInWithDocsClient || isPhoneInWithDocsAgent) {
    setLoad(false);

    phoneX = data.phone;
    window.localStorage.setItem('%^info&*ºº––', JSON.stringify(data.phone));
    navigation('/info/people');

   } else {

    setLoad(false);
    setOpen(true);
    reset()
   }
  };

  setLoad(false);
  reset();
 };
 return (
  <>
   <div className='zindex-theme'>
    <Backdrop
     sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
     open={load}>

     <CircularProgress color="inherit" />
    </Backdrop>
   </div>

   <form onSubmit={handleSubmit(onSubmit)}>

    <FormControl
     sx={{ width: '100%' }}

     variant="standard">
     <InputLabel htmlFor="standard-adornment-password">Téléphone</InputLabel>

     <Controller
      name="phone"
      defaultValue='+243'
      control={control}
      render={({ field }) =>

       <Input
        id="standard-adornment-password"
        {...field}

        className='text-input-size'
        type='phone'

       />}
     />

    </FormControl>

    <Dialog
     fullWidth={fullWidth}
     maxWidth={maxWidth}
     open={open}
     onClose={handleClose}
    >
     <DialogTitle>MuunganoMoney</DialogTitle>
     <DialogContent>

      <DialogContentText>
       Numéro non disponible chez muunganomoney
     </DialogContentText>

     </DialogContent>
     <DialogActions>
      <Button onClick={handleClose}>Fermer</Button>
     </DialogActions>
    </Dialog>

    <button className='Btn'>Continue</button>
   </form>
  </>
 )
};
