import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, onSnapshot } from "firebase/firestore";
import { db } from '../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => (
 <div className='wrp-input-authoriz-dash'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-authoriz-dash-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 const [getCode, setGetCode] = React.useState('');
 //  order notebook
 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 React.useEffect(async () => {

  try {
   await onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
    setGetCode(doc.data().code);
   });
  } catch {
   window.console.log('erreur disponible');
  }
 }, []);

 const onSubmit = async (data) => {

  if (data.code == getCode) {
   window.localStorage.setItem('?bu%^%ild--#', JSON.stringify(true));
   navigation('/reset')
  } else {
   setOpen(true);
   reset();
  }

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='password' placeholder='•••••••' name='code' {...register('code', { required: true })} />
   <button className='Btn'>Suivant</button>
   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Accès refusé, merci
     </DialogContentText>

    </DialogContent>
    <DialogActions>

     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

  </form>
 )
};
