import React from 'react';
import './Values.css';
import { money, solde } from '../FC/Input';
import Media from 'react-media';

import { doc, onSnapshot, collection, getDocs } from "firebase/firestore";
import { db } from '../firebase';
import { phoneX } from '../Sign/Input';

export let newExchAnge = 0;

// Return Value Prix
export default function ReturnValues() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-values-validExh'>
  <ViewExchAnge />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-values-validExh-sm'>
  <ViewExchAnge />
 </div>
);
export const ViewExchAnge = () => {

 const [status, setStatus] = React.useState();
 const pushArray = new Array();

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.data());
  });

  const verifier = pushArray.some(value => value == phoneX);
  const unsub = onSnapshot(doc(db, verifier ? "client" : "agent", phoneX), (doc) => {
   setStatus(doc.data().state);
  });

 }, []);

 newExchAnge = status == "agent" ? solde : money;

 return (

  <h2>{`${Math.floor(Number(newExchAnge))}`}<span>CDF</span></h2>
 );
};
