import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, getDocs, collection, updateDoc, increment, getDoc, onSnapshot } from "firebase/firestore";
import { db } from '../../../firebase';

import { phoneX } from '../../../Sign/Input';
import { sendPhone } from '../../../ValidExUsd/Input';
import { frais, Unite, solde, money } from '../../../USD/Input';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import moment from 'moment';

export let nowField = (moment().date()) - 1;

export let prixHash = 0;
export let hashTrue = true;

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => (
 <div className='wrp-input-send-money'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-send-money-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let pushDocs = new Array();
 let verifierSend;
 let verifierGet;

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 //  order notebook
 const [tableClient, setTableClient] = React.useState();
 const [tableAgent, setTableAgent] = React.useState();
 const [tableUpgrade, setTableUpgrade] = React.useState();
 const [tableAdmin, setTableAdmin] = React.useState();
 const [tableFrais, setTableFrais] = React.useState();

 // send React useState  
 const [pin, setPin] = React.useState(null);
 const [state, setState] = React.useState(null);
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 // getting React useState
 const [getstate, setGetstate] = React.useState(null);
 const [getfirst, setGetFirst] = React.useState('');
 const [getlast, setGetLast] = React.useState('');


 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 React.useEffect(async () => {
  await onSnapshot(doc(db, "muungano", "upgrade"), (doc) => {
   setTableUpgrade(doc.data().usd);
  });

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  verifierSend = pushDocs.some(value => value == phoneX);
  verifierGet = pushDocs.some(value => value == sendPhone);

  const sendRef = doc(db, verifierSend ? "client" : "agent", phoneX);
  const sendSnap = await getDoc(sendRef);

  if (sendSnap.exists()) {
   setPin(sendSnap.data().code);
   setState(sendSnap.data().state);
   setFirst(sendSnap.data().firstname);
   setLast(sendSnap.data().lastname);
  };

  const getRef = doc(db, verifierGet ? "client" : "agent", sendPhone);
  const getSnap = await getDoc(getRef);

  if (getSnap.exists()) {
   setGetstate(getSnap.data().state);
   setGetFirst(getSnap.data().firstname);
   setGetLast(getSnap.data().lastname);
  };

  try {
   await onSnapshot(doc(db, "clients", "emoney"), (doc) => {
    setTableClient(doc.data().usd);
   });

   await onSnapshot(doc(db, "agents", "emoney"), (doc) => {
    setTableAgent(doc.data().usd);
   });

   await onSnapshot(doc(db, "muungano", "upgrade"), (doc) => {
    setTableUpgrade(doc.data().usd);
   });

   await onSnapshot(doc(db, "muungano", "commission"), (doc) => {
    setTableAdmin(doc.data().usd);
   });

   await onSnapshot(doc(db, "agents", "commission"), (doc) => {
    setTableFrais(doc.data().usd);
   });

   await onSnapshot(doc(db, "muungano", "upgrade"), (doc) => {
    setTableUpgrade(doc.data().usd);
   });

  } catch {
   window.console.log('Erreur disponible');
  }


 }, []);

 const onSubmit = async (data) => {

  // Checked if value code is length valid
  if (data.code.length != 6 || pin != data.code) {
   setOpen(true);
   reset();

  } else {
   const sendUser = first.toLowerCase() + ' ' + last.toLowerCase();
   const getUser = getfirst.toLowerCase() + ' ' + getlast.toLowerCase();

   if (state == "agent" && getstate == "agent") {

    hashTrue = false;
    prixHash = solde;


    let arrayAgent = tableAgent;
    let arrayUpgrade = tableUpgrade;

    arrayAgent[nowField] = Number(tableAgent[nowField]) + solde;
    arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + solde;

    swapInWithDocsAgent(phoneX, sendPhone, sendUser, getUser, solde, Unite, arrayAgent, arrayUpgrade);
    navigation('/send-success-usd');

   } else if (state == "client" && getstate == "client") {

    hashTrue = true;
    prixHash = money;


    let arrayClient = tableClient;
    let arrayUpgrade = tableUpgrade;
    let arrayAdmin = tableAdmin;

    arrayClient[nowField] = Number(tableClient[nowField]) + Math.floor(Number(money));
    arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + Math.floor(Number(money));
    arrayAdmin[nowField] = Number(tableAdmin[nowField]) + Math.floor(Number(frais));
    isSwapInWithClientToClient(phoneX, sendPhone, sendUser, getUser, solde, money, Unite, arrayClient, arrayUpgrade, arrayAdmin);

    navigation('/send-success-usd');
   } else {

    if (state == "agent") {

     hashTrue = false;
     prixHash = solde;

     let arrayAgent = tableAgent;
     let arrayUpgrade = tableUpgrade;

     arrayAgent[nowField] = Number(tableAgent[nowField]) + solde;
     arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + solde;

     swapInWithDocsAgentToClient(phoneX, sendPhone, sendUser, getUser, solde, Unite, arrayAgent, arrayUpgrade);
     navigation('/send-success-usd');

    } else {

     hashTrue = true;
     prixHash = money;

     let arrayClient = tableClient;
     let arrayUpgrade = tableUpgrade;
     let arrayFrais = tableFrais;
     let arrayAdmin = tableAdmin;

     arrayClient[nowField] = Number(tableClient[nowField]) + Math.floor(Number(money));
     arrayUpgrade[nowField] = Number(tableUpgrade[nowField]) + Math.floor(Number(money));
     arrayAdmin[nowField] = Number(tableAdmin[nowField]) + Math.floor(Number(frais) * 70 / 100);
     arrayFrais[nowField] = Number(tableFrais[nowField]) + Math.floor(Number(frais) * 30 / 100);
     isSwapInWithClientToAgent(phoneX, sendPhone, sendUser, getUser, solde, money, Unite, arrayClient, arrayUpgrade, arrayFrais, arrayAdmin);

     navigation('/send-success-usd');

    }

   }

  }
 };


 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='password'
    placeholder='•••••••'
    name='code' {...register('code', { required: true })}
   />

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Code d'erreur (PIN) Accès non valide
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>


   <button className='Btn'>Valider</button>
  </form>
 )
};

async function swapInWithDocsAgent(sendPhone, getPhone, sendUser, getUser, prix, unite, arrayAgentMoney, upgrade) {

 const sendRef = doc(db, "agent", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: prix,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "agent", getPhone);
 await updateDoc(getRef, {
  usd: increment(prix),
  money: prix,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone,
  delay: moment().format()
 });

 const agentsemoneyRef = doc(db, "agents", "emoney");
 await updateDoc(agentsemoneyRef, {
  usd: arrayAgentMoney
 });

 const upgradeRef = doc(db, "muungano", "upgrade");
 await updateDoc(upgradeRef, {
  usd: upgrade,
 });

};
async function swapInWithDocsAgentToClient(sendPhone, getPhone, sendUser, getUser, prix, unite, arrayAgentMoney, upgrade) {

 const sendRef = doc(db, "agent", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: prix,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "client", getPhone);
 await updateDoc(getRef, {
  usd: increment(prix),
  money: prix,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone,
  delay: moment().format()
 });

 const agentsemoneyRef = doc(db, "agents", "emoney");
 await updateDoc(agentsemoneyRef, {
  usd: arrayAgentMoney,
 });

 const upgradeRef = doc(db, "muungano", "upgrade");
 await updateDoc(upgradeRef, {
  usd: upgrade,
 });

};
async function isSwapInWithClientToAgent(sendPhone, getPhone, sendUser, getUser, prix, money, unite, arrayAgentMoney, upgrade, agentFrais, adminFrais) {

 const sendRef = doc(db, "client", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: money,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "agent", getPhone);
 await updateDoc(getRef, {
  usd: increment(money),
  money: money,
  user: sendUser,
  unite: unite,
  thriftusd: increment(Math.floor(Number(frais * 30 / 100))),
  phoneclient: sendPhone,
  delay: moment().format()
 });

 const adminRef = doc(db, "muunganomoney", "money");
 await updateDoc(adminRef, {
  usd: increment(Math.floor(Number(frais * 70 / 100)))
 });

 const agentsemoneyRef = doc(db, "clients", "emoney");
 await updateDoc(agentsemoneyRef, {
  usd: arrayAgentMoney,
 });

 const upgradeRef = doc(db, "muungano", "upgrade");
 await updateDoc(upgradeRef, {
  usd: upgrade,
 });

 const agentFraisRef = doc(db, "agents", "commission");
 await updateDoc(agentFraisRef, {
  usd: agentFrais,
 });

 const fraisRef = doc(db, "muungano", "commission");
 await updateDoc(fraisRef, {
  usd: adminFrais,
 });

};
async function isSwapInWithClientToClient(sendPhone, getPhone, sendUser, getUser, prix, money, unite, arrayAgentMoney, upgrade, adminFrais) {

 const sendRef = doc(db, "client", sendPhone);
 await updateDoc(sendRef, {
  usd: increment(-(prix)),
  money: money,
  user: getUser,
  unite: unite,
  phoneclient: getPhone,
  delay: moment().format()
 });

 const getRef = doc(db, "client", getPhone);
 await updateDoc(getRef, {
  usd: increment(money),
  money: money,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone,
  delay: moment().format()
 });

 const adminRef = doc(db, "muunganomoney", "money");
 await updateDoc(adminRef, {
  usd: increment(Math.floor(Number(frais)))
 });

 const agentsemoneyRef = doc(db, "clients", "emoney");
 await updateDoc(agentsemoneyRef, {
  usd: arrayAgentMoney,
 });

 const upgradeRef = doc(db, "muungano", "upgrade");
 await updateDoc(upgradeRef, {
  usd: upgrade
 });

 const fraisRef = doc(db, "muungano", "commission");
 await updateDoc(fraisRef, {
  usd: adminFrais,
 });

};
