import React from 'react';
import { db } from '../../firebase';
import { doc, onSnapshot, collection, getDocs } from "firebase/firestore";
import { phoneX } from '../../Phone/Input';

import './Swap.css';

let range = [
 {
  date: '2022-04-11',
  quantiter: 23883,
  unite: 'CDF',
  user: 'aline makoso'
 },
 {
  date: '2022-12-24',
  quantiter: 483,
  unite: 'USD',
  user: 'jack mukuli'
 },
 {
  date: '2022-02-24',
  quantiter: 87500,
  unite: 'CDF',
  user: 'julie osama'
 },
 {
  date: '2022-10-05',
  quantiter: 6500,
  unite: 'CDF',
  user: 'henry mabila'
 },
 {
  date: '2022-12-01',
  quantiter: 12700,
  unite: 'CDF',
  user: 'luoma kulia'
 },

];

let pushDocs = new Array();

// Swap Continue 
export default function ReturnSwAp() {

 const [toSwap, setToSwap] = React.useState([]);

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushDocs.push(doc.id);
  });

  const verifier = pushDocs.some(value => value == phoneX);
  const unsub = onSnapshot(doc(db, verifier ? "client" : "agent", phoneX), (doc) => {
   setToSwap(doc.data().swap);
  });

 }, [])

 return (
  <div className='wrp-swap-list'>
   {
    [...toSwap].map((value) => {

     let x = Object.values(value);
     return (
      <div className='wrp-swap-sub-list'>
       {
        [...x].map(index => {
         return (
          <h2>{index}</h2>
         )
        })
       }
      </div>
     )
    })
   }
  </div>
 );
};
