import React from 'react';
import './Dollar.css';
import ReturnImg from './Img';
import Media from 'react-media';
import ReturnUnite from './Unite';

import { db } from '../firebase';
import { doc, getDoc } from "firebase/firestore";


// Return Solde dollars
export default function ReturnSoldeDollaRs() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-solde-prix-dash'>
  <ReturnImg img={'/img/dollars.png'} />
  <ViewUnite />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-solde-prix-dash-sm'>
  <ReturnImg img={'/img/dollars.png'} />
  <ViewUnite />
 </div>
);
export const ViewUnite = () => {

 const [prix, setPrix] = React.useState(null);

 React.useEffect(async () => {

  const docRef = doc(db, "muunganomoney", "money");
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {

   setInterval(() => { })

   setPrix(docSnap.data().usd);
  };

 }, []);

 return (
  <div className='wrp-view-unite-dash'>
   <div className='wrp-prix-dash'><h2>{prix}</h2></div>

   <ReturnUnite Text={'USD'} />
  </div>
 );
};
