import React from 'react';
import './Input.css';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { auth } from '../../firebase';
import {
 createUserWithEmailAndPassword,
 signInWithEmailAndPassword, onAuthStateChanged
} from "firebase/auth";

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';
import { doc, updateDoc, increment, onSnapshot, arrayUnion } from "firebase/firestore";
import moment from 'moment';

let now = moment().date();


// Input form field 
export default function ReturnInPut() {

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 const [open, setOpen] = React.useState(false);
 const [currentUser, setCurrentUser] = React.useState(null);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');


 // Array Admin emoney and 
 const [tableEmCdf, setTableEmCdf] = React.useState();
 const [tableAgentCdf, setTableAgentCdf] = React.useState();


 React.useEffect(async () => {
  onAuthStateChanged(auth, user => { setCurrentUser(user) });
 }, [currentUser]);


 React.useEffect(async () => {

  try {
   await onSnapshot(doc(db, "muungano", "emoney"), (doc) => {
    setTableEmCdf(doc.data().cdf);
   });
   await onSnapshot(doc(db, "agents", "emoney"), (doc) => {
    setTableAgentCdf(doc.data().cdf);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);


 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 // Ref collection database!
 const onSubmit = async (data) => {

  // await createUserWithEmailAndPassword(auth, data.email, data.password)
  //   .then((userCredential) => {
  //     // Signed in 
  //     const user = userCredential.user;
  //     console.log(user);
  //   })
  //   .catch((error) => {
  //     const errorCode = error.code;
  //     const errorMessage = error.message;
  //     window.console.log(errorCode + ' ' + errorMessage);
  //     setOpen(true);
  //     // window.alert(`L'accès n'est pas autorisé,e-mail ou mot de passe administrateur, n'est pas valide.`);
  //     reset();
  //   });



  await signInWithEmailAndPassword(auth, data.email, data.password)
   .then((userCredential) => {

    // Signed in 
    const user = userCredential.user;
    if (user.uid != null) {
     window.localStorage.setItem('ACTIVE', JSON.stringify(true));
     navigation('/dash');
    }

   })
   .catch((error) => {

    const errorCode = error.code;
    const errorMessage = error.message;
    window.console.log(errorCode + ' ' + errorMessage);
    setOpen(true);
    reset();
   });


  // Admin Input
  let soldeEmCDF = Number(Math.max(tableEmCdf)) + Number(data.count);
  let soldeAgentCDF = Number(Math.max(tableAgentCdf)) + Number(data.count);

  let arrayEmCdf = tableEmCdf;
  let arrayAgentCdf = tableAgentCdf;

  for (let i = now; i <= 31; i++) {
   arrayEmCdf[i] = soldeEmCDF;
   arrayAgentCdf[i] = soldeAgentCDF;
  }



 };

 return (
  <div className='wrp-input-sign'>
   <form onSubmit={handleSubmit(onSubmit)}>
    <input type='text' placeholder='example@email.com'  {...register('email', { required: true })} />
    <input type='password' placeholder='•••••••'  {...register('password', { required: true })} />
    <button className='Btn'>Connexion</button>
   </form>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      L'accès n'est pas autorisé,e-mail ou mot de passe administrateur, n'est pas valide.
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

  </div>
 )
};


async function updateMuungano(emCdf, agentCdf) {

 const emoneyRef = doc(db, "muungano", "emoney");
 await updateDoc(emoneyRef, {
  cdf: emCdf,
 });

 const agentsemoneyRef = doc(db, "agents", "emoney");
 await updateDoc(agentsemoneyRef, {
  cdf: agentCdf,
 });

};
