import React from 'react';
import './Signout.css';
import { signOut } from "firebase/auth";
import { auth } from '../firebase';
import Media from 'react-media';

// Return View Sign Out
export default function ReturnSignOut() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-btn-signout'>
  <ViewButtonSignOut />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-signout-sm'>
  <ViewButtonSignOut />
 </div>
);
export const ViewButtonSignOut = () => {

 const [showOut, setShowOut] = React.useState(null);
 const handleSignOut = (event) => {
  event.preventDefault();

  signOut(auth);

  window.localStorage.setItem('ACTIVE', JSON.stringify(false));
  const data = window.localStorage.getItem('ACTIVE');
  setShowOut(JSON.parse(data));

  if (showOut === false) {
   window.location.href = "/";
  }

 };

 return (
  <button className='Btn-Red' onClick={handleSignOut}>DÃ©connexion</button>
 )
};
