import React from 'react';
import './Input.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Media from 'react-media';

import { useForm } from 'react-hook-form';


// Input Field form
export default function REturnInPut() {

 const [btn, setBtn] = React.useState(true);
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => {

 const [handlechangeinput, setHandlechangeinput] = React.useState(true);
 const { register, handleSubmit } = useForm({
  defaultValues: {
   phone: '+243'
  }
 });

 const handleInput = (event) => {
  setHandlechangeinput(false);
 }

 const onsubmit = (data) => {
  console.log(data);
 }

 return (
  <div className='form-input-sign'>

   <form onSubmit={handleSubmit(onsubmit)}>
    {handlechangeinput ?
     <input type='phone' focus placeholder='Telephone' name='phone'  {...register('phone', { required: true })} />
     :
     <input type='phone' focus placeholder='code' name='code'  {...register('phone', { required: true })} />
    }

    <button
     onClick={() => handleInput}
     className='btn'>Suivant</button>

   </form>

  </div>
 )
};
export const ScreenSmall = () => (
 <div className='form-input-sign-sm'>
  <form>
   <input type='text' placeholder='Telephone numero ' name='phone' />
   <Button>Suivant</Button>
  </form>
 </div>
);
