import React from 'react';
import './Prix.css';
import Media from 'react-media';

// REturn view Prix available
export default function REturnPriX(props) {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall count={props.count} />}
     {matches.medium && <ScreenLarge count={props.count} />}
     {matches.large && <ScreenLarge count={props.count} />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = (props) => (
 <div className='wrp-prix-send'>
  <h2>Balance disponible</h2>
  <CountPrix count={props.count} />
  <span>Solde</span>
 </div>
);
export const ScreenSmall = (props) => (
 <div className='wrp-prix-send-sm'>
  <h2>Balance disponible</h2>
  <CountPrix count={props.count} />
  <span>Solde</span>
 </div>
);
function CountPrix(props) {
 const [prix, setPrix] = React.useState(56);
 return (
  <p>{`${prix - props.count}`}</p>
 );
};
