import React from 'react';
import './BtnCurrent.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import GridLoader from 'react-spinners/GridLoader';

// Button Current Component
export default function ReturnButtonCuRRent() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-btn-current-account'>
  <ViewText />
  <HanDleNavigation />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-current-account-sm'>
  <ViewText />
  <HanDleNavigation />
 </div>
);
export const HanDleNavigation = () => {
 return (
  <ViewButton />
 );
};

const ViewText = () => (
 <p>
  Commencer à accepter le e-money sur votre site Web MuuganoMoney en quelques étapes,
  tous les paiements via BTC,
 </p>
);
const ViewButton = () => {

 const [loading, setLoading] = React.useState(false);
 const navigation = useNavigate();

 const handlepath = (event) => {
  event.preventDefault();
  setLoading(true);

  setTimeout(() => {
   setLoading(false);
   navigation('/current-money');
  }, 3600);

 };

 return (
  <div>
   {
    loading ?
     <div className='App-loading'>
      <GridLoader
       size={15}
       color={'#123abc'}
       loading={loading}
      />
     </div>
     :
     <button onClick={handlepath} className='Btn'>Portefeuilles</button>
   }
  </div>
 )
};
