import React from 'react';
import './Phone.css';
import { IoMdCall } from 'react-icons/io';
import Media from 'react-media';
import { phoneX } from '../Sign/Input';

import { collection, getDocs, doc, getDoc } from "firebase/firestore";
import { db } from '../firebase';


// This is Name view 
export default function ReturnPhoneSetting() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-name-phone'>
  <div className='wrp-boxname-phone'>
   <IoMdCall size={'20px'} color={'grey'} />

   <div className='phone-name-identity'>
    <h1>Télèphone</h1>
    <PhoneView />
    <ViewText />
   </div>

   {/* <IoMdCreate size={'20px'} color={'#ffd600'} /> */}
  </div>
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-name-phone-sm'>
  <div className='wrp-boxname-phone-sm'>
   <IoMdCall size={'35px'} color={'grey'} />

   <div className='phone-name-identity-sm'>
    <h1>Télèphone</h1>
    <PhoneView />
    <ViewText />
   </div>

  </div>
 </div>
);
export const ViewText = () => (
 <p>Télèphone utilisateur avec MuunganoMoney compte .</p>
);
export const PhoneView = () => {

 const [numPhone, setNumPhone] = React.useState(null);
 let pushDocs = new Array();

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  let collections = pushDocs.some(x => x == phoneX);
  const docRef = doc(db, collections ? 'client' : 'agent', phoneX);
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setNumPhone((docSnap.data().phone))
  }
 }, []);

 return (
  <h2>{numPhone}</h2>
 )
};
