import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, getDocs, collection, getDoc, updateDoc, increment, serverTimestamp } from "firebase/firestore";
import { db } from '../../../firebase';

import { phoneX } from '../../../Sign/Input';
import { sendPhone } from '../../../ValidExFran/Input';
import { frais, Unite, solde, money } from '../../../FC/Input';

export let prixHash = 0;
export let hashTrue = true;

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-input-phone-register'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-phone-register-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let pushDocs = new Array();
 let verifierSend;
 let verifierGet;

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 // send React useState  
 const [pin, setPin] = React.useState(null);
 const [state, setState] = React.useState(null);
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 // getting React useState
 const [getstate, setGetstate] = React.useState(null);
 const [getfirst, setGetFirst] = React.useState('');
 const [getlast, setGetLast] = React.useState('');


 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

  verifierSend = pushDocs.some(value => value == phoneX);
  verifierGet = pushDocs.some(value => value == sendPhone);

  const sendRef = doc(db, verifierSend ? "client" : "agent", phoneX);
  const sendSnap = await getDoc(sendRef);

  if (sendSnap.exists()) {
   setPin(sendSnap.data().code);
   setState(sendSnap.data().state);
   setFirst(sendSnap.data().firstname);
   setLast(sendSnap.data().lastname);
  };

  const getRef = doc(db, verifierGet ? "client" : "agent", sendPhone);
  const getSnap = await getDoc(getRef);

  if (getSnap.exists()) {
   setGetstate(getSnap.data().state);
   setGetFirst(getSnap.data().firstname);
   setGetLast(getSnap.data().lastname);
  };

 }, []);

 const onSubmit = async (data) => {

  // Checked if value code is length valid
  if (data.code.length != 6 || pin != data.code) {
   window.alert(`Code d'erreur (PIN) Acc√®s non valide`);
   reset();
  } else {
   const sendUser = first.toLowerCase() + ' ' + last.toLowerCase();
   const getUser = getfirst.toLowerCase() + ' ' + getlast.toLowerCase();

   if (state == "agent" && getstate == "agent") {

    hashTrue = false;
    prixHash = solde;
    console.log('agent');
    swapInWithDocsAgent(phoneX, sendPhone, sendUser, getUser, solde, Unite);
    navigation('/send-success');

   } else if (state == "client" && getstate == "client") {

    hashTrue = true;
    prixHash = money;
    console.log('client');
    swapInWithDocsClient(phoneX, sendPhone, sendUser, getUser, solde, money, Unite, frais);
    navigation('/send-success');
   } else {

    hashTrue = true;
    prixHash = money;

    isSwapInWithAgent(phoneX, sendPhone, sendUser, getUser, solde, money, Unite);
    console.log('other');
    navigation('/send-success');

   }

  }
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='password'
    placeholder='Code'
    name='code' {...register('code', { required: true })}
   />
   <button className='Btn'>Valider</button>
  </form>
 )
};


async function swapInWithDocsAgent(sendPhone, getPhone, sendUser, getUser, prix, unite) {

 const sendRef = doc(db, "agent", sendPhone);
 await updateDoc(sendRef, {
  cdf: increment(-(prix)),
  money: prix,
  user: getUser,
  unite: unite,
  phoneclient: getPhone
 });

 const getRef = doc(db, "agent", getPhone);
 await updateDoc(getRef, {
  cdf: increment(prix),
  money: prix,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone
 });

};
async function swapInWithDocsClient(sendPhone, getPhone, sendUser, getUser, prix, money, unite, frais) {

 const sendRef = doc(db, "client", sendPhone);
 await updateDoc(sendRef, {
  cdf: increment(-(prix)),
  money: money,
  user: getUser,
  unite: unite,
  phoneclient: getPhone

 });

 const getRef = doc(db, "client", getPhone);
 await updateDoc(getRef, {
  cdf: increment(money),
  money: money,
  user: sendUser,
  unite: unite,
  phoneclient: sendPhone
 });

 const commissionRef = doc(db, "muunganomoney", "commission");
 await updateDoc(commissionRef, {
  cdf: increment(Math.floor(Number(frais)))
 });

 const adminRef = doc(db, "muunganomoney", "money");
 await updateDoc(adminRef, {
  cdf: increment(Math.floor(Number(frais)))
 });

};
async function isSwapInWithAgent(sendPhone, getPhone, sendUser, getUser, prix, money, unite) {

 const sendRef = doc(db, "agent", sendPhone);
 await updateDoc(sendRef, {
  cdf: increment(-(prix)),
  money: money,
  user: getUser,
  delay: serverTimestamp(),
  unite: unite,
  thrift: increment(Math.floor(Number(frais * 30 / 100))),
  phoneclient: getPhone
 });

 const getRef = doc(db, "client", getPhone);
 await updateDoc(getRef, {
  cdf: increment(money),
  money: money,
  user: sendUser,
  delay: serverTimestamp(),
  unite: unite,
  phoneclient: sendPhone
 });

 const adminCommissionRef = doc(db, "muunganomoney", "commission");
 await updateDoc(adminCommissionRef, {
  cdf: increment(Math.floor(Number(frais * 70 / 100)))
 });

 const adminRef = doc(db, "muunganomoney", "money");
 await updateDoc(adminRef, {
  cdf: increment(Math.floor(Number(frais * 70 / 100)))
 });

};
