import './Avatar.css';
import { IoIosPerson, IoMdCamera } from 'react-icons/io';
import Media from 'react-media';

// Avatar IMAGE VIew
export default function ReturnAvataR() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-avatar-profil'>

  <div className='avatar-circle-profil'>
   <IoIosPerson size={'4em '} />
  </div>

  <div className='avatar-edit'>
   <IoMdCamera size={'1em'} />
  </div>

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-avatar-profil-sm'>
  <div className='avatar-circle-profil-sm'>
   <IoIosPerson size={'3em'} />
  </div>
  <div className='avatar-edit'>
   <IoMdCamera size={'1em'} />
  </div>

 </div>
);
