import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, updateDoc, increment, onSnapshot } from "firebase/firestore";
import { db } from '../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

import moment from 'moment';

// Firebase auth 
export let phoneX = '';
export let nowField = (moment().date()) - 1;

var regular = /[a-z]+/;

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-input-add-dash'>
  <FormInput />

 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-add-dash-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 const [getCode, setGetCode] = React.useState('');
 //  order notebook
 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 React.useEffect(async () => {

  try {

   await onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
    setGetCode(doc.data().code);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 const onSubmit = async (data) => {

  if (data.code == getCode) {
   if (Number(data.cdf) > 2000000 || Number(data.usd) > 1000 || regular.test(data.usd) || regular.test(data.cdf)) {

    setOpen(true);
    reset();

   } else {

    incrementEmoney(Math.floor(Number(data.cdf)), Math.floor(Number(data.usd)));
    navigation('/dash');

   }

  } else {
   setOpen(true);
   reset();
  }
  reset();
 };
 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type='text' placeholder='USD' name='usd' {...register('usd', { required: true })} />
   <input type='text' placeholder='CDF' name='cdf' {...register('cdf', { required: true })} />
   <input type='password' placeholder='••••••' name='code' {...register('code', { required: true })} />

   <button className='Btn'>Ajouter</button>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Vérifiez le code d'accès. Ou l'équilibre est insuffisant
     </DialogContentText>

    </DialogContent>
    <DialogActions>

     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

  </form>
 )
};

async function incrementEmoney(prixCdf, prixUsd) {
 const washingtonRef = doc(db, "muunganomoney", "money");
 // Atomically increment the population of the city by 50.
 await updateDoc(washingtonRef, {
  cdf: increment(prixCdf),
  usd: increment(prixUsd),
 });
};
