import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';

// Firebase auth 
export let phoneX = '';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};
export const SreenLarge = () => (
 <div className='wrp-input-phone-register'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-input-phone-register-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 let clientDocs = new Array();
 let agentDocs = new Array();
 let regular = /[a-z]+/;

 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm({ defaultValues: { phone: '+243' } });

 const [checked, setChecked] = React.useState(true);
 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');

 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 const onSubmit = async (data) => {

  if (data.phone.length != 13 || regular.test(data.phone)) {
   setOpen(true);
   reset();

  } else {

   const clientSnapshot = await getDocs(collection(db, "client"));
   clientSnapshot.forEach((doc) => {
    clientDocs.push(doc.id);
   });

   const agentSnapshot = await getDocs(collection(db, "agent"));
   agentSnapshot.forEach((doc) => {
    agentDocs.push(doc.id);
   });

   const checkedNumPhoneClient = clientDocs.some(value => value == data.phone);
   const checkedNumPhoneAgent = agentDocs.some(value => value == data.phone);

   if (checkedNumPhoneClient || checkedNumPhoneAgent) {
    setOpen(true);
    reset();
   } else {

    phoneX = data.phone;
    navigation('/register/signin');
   }

  };
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text'
    placeholder='Phone'
    name='phone' {...register('phone', { required: true })}
   />
   <button className='Btn'>Continue</button>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Numero de télèphone entre est déja enregistre chez MuunganoMoney
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

  </form>
 );
};
