import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { } from 'react-router-dom';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};


export const SreenLarge = () => {

 const { register, handleSubmit, setValue } = useForm({ defaultValues: { phone: '' } })

 return (
  <div className='wrp-input-phone-register'>
   <form onSubmit={handleSubmit((data) => console.log('submitted', data))}>
    <input type='text' placeholder='Phone' name='phone' {...register('phone')} />

    <div className='wrp-btn'>
     <button onClick={() => { setValue('phone', '+243') }}>Continue</button>
    </div>

   </form>
  </div >
 );
};
export const ScreenSmall = () => {

 const { register, handleSubmit, setValue } = useForm({ defaultValues: { phone: '' } })

 return (
  <div className='wrp-input-phone-register-sm'>
   <form onSubmit={handleSubmit((data) => console.log('Submitted', data))}>
    <input type='text' placeholder='Phone' name='phone' {...register('phone')} />
    <div className='wrp-btn'>
     <button onClick={() => { setValue('phone', '+243') }} className='btn-sm'>Continue</button>
    </div>
   </form>
  </div>
 )
}
