import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => {

 const { register, handleSubmit } = useForm({});
 const onSubmit = (data) => {
  console.log(data);
 };

 const navigation = useNavigate();

 const handlepath = (event) => {
  event.preventDefault();
  navigation('/register-save');
 }

 return (
  <div className='wrp-input-signin-register'>

   <FormInput />

  </div>
 )
}
export const ScreenSmall = () => {

 const { register, handleSubmit } = useForm({});
 const onSubmit = (data) => {
  console.log(data);
 };

 return (
  <div className='wrp-input-signin-register-sm'>

   <form onSubmit={handleSubmit(onSubmit)}>
    <input type='text' placeholder='Nom' name='nom' {...register('nom', { required: true })} />
    <input type='text' placeholder='Prenom' name='prenom' {...register('prenom', { required: true })} />
    <input type='text' placeholder='Ville' name='ville' {...register('ville', { required: true })} />
    <input type='text' placeholder='Addresse' name='addresse' {...register('addresse', { required: true })} />
    <input type='date' placeholder='Date de naissance' name='data' {...register('data', { required: true })} />
    <button className='btn-sm'>Enregistre</button>

   </form>
  </div>
 )
};



export const FormInput = () => {


 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text' placeholder='Nom' name='nom' {...register('nom', { required: true })} />
   <input type='text' placeholder='Prenom' name='prenom' {...register('prenom', { required: true })} />
   <input type='text' placeholder='Ville' name='ville' {...register('ville', { required: true })} />
   <input type='text' placeholder='Addresse' name='addresse' {...register('addresse', { required: true })} />
   <input type='date' placeholder='Date de naissance' name='data' {...register('data', { required: true })} />
   <button>Enregistre</button>
  </form>
 )
}
