import React from 'react';
import './Info.css';
import Media from 'react-media';
import { doc, onSnapshot, collection, getDocs } from "firebase/firestore";
import { db } from '../../firebase';

let pushArray = new Array();

// Return view info
export default function ReturnInfo() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-info-client'>
  <VieWInfo />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-info-client-sm'>
  <VieWInfo />
 </div>
);
export const VieWInfo = () => {

 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.id);
  });


  let phoneRemove = window.localStorage.getItem('PHONE_USER_REMOVE');
  // setLastPhone(JSON.parse(phoneRemove))

  const verifier = pushArray.some(value => value == JSON.parse(phoneRemove));
  const unsub = onSnapshot(doc(db, verifier ? "client" : "agent", JSON.parse(phoneRemove)), (doc) => {
   setFirst(doc.data().firstname);
   setLast(doc.data().lastname);

   // setState(doc.data().state);
   // setAdresse(doc.data().addresse);
   // setPhone(doc.data().phone);
   // setPassword(doc.data().code);
   // setBirth(doc.data().birth);
   // setAccountdate(doc.data().accountdate);
  });

 }, []);

 return (
  <nav>
   <ul>
    <li><span>Statut</span>: <span>Agent</span></li>
    <li><span>date de naissance</span>: <span>20 mars 2019</span></li>
    <li><span>date de création</span>: <span>20 juin 2010</span></li>
   </ul>
  </nav>
 )
}
