import './Subject.css';
import Media from 'react-media';

// This is SubJect
export default function ReturnSubJect() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-subject-homed'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-subject-homed-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => (
 <h1>
  La monnaie électronique est définie au sens large comme un dispositif technique qui peut être largement utilisé pour effectuer des paiements à des entités autres que l'émetteur de monnaie électronique.
 </h1>
);
