import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, setDoc, getFirestore } from 'firebase/firestore';
import { auth } from '../../firebase';
import { confirmPasswordReset, signInWithPhoneNumber } from 'firebase/auth';
import { Current } from '../../Money';


// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => {
 return (
  <div className='wrp-input-signin-register'>
   <FormInput />

  </div>
 )
}
export const ScreenSmall = () => {
 return (
  <div className='wrp-input-signin-register-sm'>
   <FormInput />

  </div>
 )
};


export const FormInput = () => {

 // const [phoneNumber, setPhoneNumber] = React.useState('');
 const navigation = useNavigate();
 const db = getFirestore();


 const { register, handleSubmit } = useForm({
  defaultValues: {
   phone: '+243'
  }
 });


 // Ref collection database!
 const onSubmit = async (data) => {

  // const uid = '';

  // signInWithPhoneNumber(auth, signInWithPhoneNumber).then((userCredentia) => {
  //  const user = userCredentia;
  //  console.log(user.uid);
  //  navigation('/register/success');
  // }).catch((error) => { console.log(error.message) });


  const clientdata = Object.assign(data, { timestamp: new Date() })
  console.log(clientdata);

  const clientRef = doc(db, 'Client', data.phone); // Ref documents !

  await setDoc(clientRef, data, { merge: true }).then((current) => {
   data.phone.length != 13 ? window.alert('Numero incorrect.') : navigation('/register/success');
  }).catch(error => {
   console.log(error.message)
  });

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text' placeholder='Nom' name='firstname' {...register('firstname', { required: true })} />
   <input type='text' placeholder='Prenom' name='lastname' {...register('lastname', { required: true })} />
   <input type='email' placeholder='Email' name='email' {...register('email', { required: false })} />
   <input type='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
   <input type='text' placeholder='Ville' name='ville' {...register('ville', { required: true })} />
   <input type='text' placeholder='Addresse' name='addresse' {...register('addresse', { required: true })} />
   <input type='date' placeholder='date de naissance' name='birthey' {...register('birthey', { required: true })} />
   <button className='Btn'>Enregistre</button>
  </form>
 )
}
