    date: time,
    solde: `${money} ${unite}`,
    phone: getPhone,
    user: getUser,
    type: 'envoyer'
  };
  let get = {
    date: time,
    solde: `${money} ${unite}`,
    phone: sendPhone,
    user: sendUser,
    type: 'réussi'
  };
  const sendRef = doc(db, "agent", sendPhone);
  await updateDoc(sendRef, {
    cdf: main,
    money: money,
    user: getUser,
    unite: unite,
    phoneclient: getPhone,
    delay: moment().format(),
    swap: arrayUnion(send)
  });
  const getRef = doc(db, "agent", getPhone);
  await updateDoc(getRef, {
    cdf: increment(money),
    money: money,
    user: sendUser,
    unite: unite,
    phoneclient: sendPhone,
    delay: moment().format(),
    swap: arrayUnion(get)
  });
  const agentsemoneyRef = doc(db, "agents", "emoney");
  await updateDoc(agentsemoneyRef, {
    cdf: arrayAgentMoney
  });
  const upgradeRef = doc(db, "muungano", "upgrade");
