import React from 'react';
import './Identify.css';
import { doc, onSnapshot, collection, getDocs } from "firebase/firestore";
import { db } from '../firebase';
import { phoneX } from '../Phone/Input';
import Media from 'react-media';
import moment from 'moment';

export let pushArray = new Array();


// ReturnIdentify Component
export default function ReturnIdentify() {

 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');
 const [state, setState] = React.useState('');
 const [phone, setPhone] = React.useState('');

 const [adresse, setAdresse] = React.useState('');
 const [password, setPassword] = React.useState('');
 const [birth, setBirth] = React.useState('');
 const [accountdate, setAccountdate] = React.useState('');

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.id);
  });
  const verifier = pushArray.some(value => value == phoneX);
  const unsub = onSnapshot(doc(db, verifier ? "client" : "agent", phoneX), (doc) => {
   setFirst(doc.data().firstname);
   setLast(doc.data().lastname);
   setState(doc.data().state);
   setAdresse(doc.data().addresse);
   setPhone(doc.data().phone);
   setPassword(doc.data().code);
   setBirth(doc.data().birth);
   setAccountdate(doc.data().accountdate);
  });

 }, []);

 return (
  <div className='wrp-identify-last'>

   <UserIDentify Type={'Nom'} Text={`${first.toLowerCase()} ${last.toLowerCase()}`} />
   <UserIDentify Type={'Status'} Text={`${state}`} />
   <UserIDentify Type={'Téléphone'} Text={`${phone}`} />
   <UserIDentify Type={'Adresse'} Text={`${adresse}`} />

   <UserIDentify Type={'Password'} Text={`${password}`} />
   <UserIDentify Type={'Date de naissance'} Text={`${birth}`} />
   <UserIDentify Type={'Date de création'} Text={`${moment(accountdate).format('L')}`} />

  </div>
 );
};


export const UserIDentify = (props) => {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall Type={props.Type} Text={props.Text} />}
     {matches.medium && <ScreenLarge Type={props.Type} Text={props.Text} />}
     {matches.large && <ScreenLarge Type={props.Type} Text={props.Text} />}
    </>
   )}
  </Media>
 )
};

export const ScreenLarge = (props) => (
 <IdentifyTypeLG Type={props.Type} Text={props.Text} />
);
export const ScreenSmall = (props) => (
 <IdentifyTypeSM Type={props.Type} Text={props.Text} />
);

export const IdentifyTypeLG = (props) => {
 return (
  <div className='wrp-user-identify'>
   <h2>{props.Type} :</h2>
   <h2>{props.Text}</h2>
  </div>
 );
};
export const IdentifyTypeSM = (props) => {
 return (
  <div className='wrp-user-identify-sm'>
   <h2>{props.Type} :</h2>
   <h2>{props.Text}</h2>
  </div>
 );
};
