import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { TweenMax, Expo } from 'gsap';
import { useNavigate } from 'react-router-dom';

// Register Btn view
export default function BtnRegion() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => {
 return (
  <div className='wrp-regionbtn'>
   <ScreenBtnPath />
  </div>
 )
};
export const ScreenSmall = () => {
 return (
  <div className='wrp-regionbtn-sm'>
   <ScreenBtnPath />
  </div>
 )
};


export const ScreenBtnPath = () => {

 React.useEffect(() => {
  TweenMax.from('.wrp-regionbtn button', 1.2, { delay: 1.3, opacity: 0, y: 20, ease: Expo.easeIn });
 }, []);

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  navigation('/register-phone');
 };

 return (
  <button onClick={handlepath}>Continue</button>
 );

}
