import React from 'react';
import ReturnCurrent from './Current';
import { auth } from '../firebase';
import SignUp from '../Sign/Main';
import BarLoader from 'react-spinners/BarLoader';

// Current Main 
export default function CuRRent() {

 const user = auth.currentUser;
 const [loading, setLoading] = React.useState(true);
 const [people, setPeople] = React.useState(user);

 React.useEffect(() => {

  // setLoading(true);
  setTimeout(() => {
   setLoading(false);
  }, 4600);

  if (user) {
   setTimeout(() => {
    setPeople(true);
   }, 600);

  }
 }, []);

 return (
  <div>
   {loading && <div className='App-loading-Current'></div>}
   {loading && <div className='App-Icon'>
    <BarLoader
     size={15}
     color={'#ffeb3b'}
     loading={loading}
    />
   </div>}

   <>
    {people ? <ReturnCurrent /> : <SignUp />}
   </>
  </div>
 );
};
