import React from 'react';
import './Arrow.css';
import { HiArrowLeft } from 'react-icons/hi';
import { TweenMax, Expo } from 'gsap';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';


// REturn Arrow component 
export default function REturnArroW() {

 React.useEffect(() => {
  TweenMax.from('.wrp-arrow-profil .wrp-icon', 1.2, { delay: .4, opacity: 0, x: 20, ease: Expo.easeInOut });
 }, [])

 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => {
 return (
  <div className='wrp-arrow-profil-sign'>
   <Arrow />
  </div>
 )
};
export const ScreenSmall = () => {
 return (
  <div className='wrp-arrow-profil-sign-sm'>
   <Arrow />
  </div>
 )
};
export const Arrow = () => {
 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  navigation(-1);
 };
 return (
  <div onClick={handlepath} className='wrp-icon'>
   <HiArrowLeft size={'1.8em'} />
  </div>
 );
};
