import React from 'react';
import './Input.css';
import REturnPriX from './Prix';
import Media from 'react-media';
import {
 useForm
} from 'react-hook-form';


export let available = 20;

// Return Phone input component
export default function REturnInputPhone() {
 return (
  <>
   <Media
    queries={{
     small: '(max-width: 599px)',
     medium: '(min-width: 600px) and (max-width:1199px)',
     large: '(min-width: 1200px)',
    }}>
    {matches => (
     <>
      {matches.small && <ScreenSmall />}
      {matches.medium && <ScreenLarge />}
      {matches.large && <ScreenLarge />}
     </>
    )}
   </Media>
  </>
 );
};

export const ScreenLarge = () => {

 const [prix, setPrix] = React.useState(200);
 const { register, handleSubmit, watch } = useForm();
 const onSubmit = data => {
  console.log(data);
 };

 let value = 0;

 let entry = watch('count');
 if (value <= 0) {
  value = null;
 }
 value = entry === undefined ? prix : prix - entry;

 let frais = (entry * 3) / 100;

 frais = value - frais;
 console.log((prix * 3) / 100);

 return (
  <>

   <REturnPriX count={value} />
   <div>{`${3}% Frais ${frais}`}</div>

   <div className='wrp-inputphone-send'>
    <h2>Entre numero du beneficier</h2>
    <form onSubmit={onSubmit}>
     <input type={'text'} name="phone" />
     <input type={'text'} {...register('count')} name="count" placeholder={'0'} onFocus className='input-count' />

     <button className='btn'>Suivant</button>
    </form>

   </div>
  </>
 );
};
export const ScreenSmall = () => {

 const [prix, setPrix] = React.useState(200);
 const { register, handleSubmit, watch } = useForm();
 const onSubmit = data => {
  console.log(data);
 };

 let entry = watch('count');
 let value = entry === undefined ? prix : prix - entry;
 let frais = (entry * 3) / 100;

 frais = value - frais;
 console.log((prix * 3) / 100);

 return (
  <>
   <REturnPriX count={value} />
   <div>{`${3}% Frais ${frais}`}</div>
   <div className='wrp-inputphone-send-sm'>
    <h2>Entre numero du beneficier</h2>

    <form>
     <input type={'text'} name="phone" />
     <input type={'text'} name="count" {...register('count')} placeholder={'0'} onFocus className='input-count-sm' />
     <button className='btn'>Suivant</button>
    </form>

   </div>
  </>
 )
}
