import React from 'react';
import './Info.css';
import Media from 'react-media';
import { doc, collection, getDocs, getDoc } from "firebase/firestore";
import { db } from '../../firebase';
import moment from 'moment';


let pushArray = new Array();

// Return view info
export default function ReturnInfo() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-info-client'>
  <VieWInfo />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-info-client'>
  <VieWInfo />
 </div>
);
export const VieWInfo = () => {

 const [state, setState] = React.useState();
 const [adresse, setAdresse] = React.useState();
 const [phone, setPhone] = React.useState();
 const [birth, setBirth] = React.useState();
 const [accountdate, setAccountdate] = React.useState();
 const [soldeCdf, setSoldeCdf] = React.useState();
 const [soldeUsd, setSoldeUsd] = React.useState();


 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.id);
  });

  const verifier = pushArray.some(value => value == JSON.parse(window.localStorage.getItem('rm #@!!/[] -')));
  const docRef = doc(db, verifier ? "client" : "agent", JSON.parse(window.localStorage.getItem('rm #@!!/[] -')));
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {

   setState(docSnap.data().state);
   setAdresse(docSnap.data().addresse);
   setPhone(docSnap.data().phone);
   setBirth(docSnap.data().birth);
   setAccountdate(docSnap.data().accountdate);
   setSoldeCdf(docSnap.data().cdf);
   setSoldeUsd(docSnap.data().usd);

  } else {
   // doc.data() will be undefined in this case
   console.log("No such document!");
  }


 }, []);

 window.localStorage.setItem('@st#!--%#', JSON.stringify(state));

 return (
  <nav>
   <ul>
    <li><span>Statut:</span><span className='wrp-color-info'>{state}</span></li>
    <li><span>Adresse:</span><span className='wrp-color-info'>{adresse}</span></li>
    <li><span>Téléphone:</span><span className='wrp-color-info'>{phone}</span></li>
    <li><span>Date de naissance:</span><span className='wrp-color-info'>{birth}</span></li>
    <li><span>Solde:</span>
     <span>{soldeUsd} <span className='wrp-color-info'>USD</span></span>
     <span>{soldeCdf} <span className='wrp-color-info'>CDF</span></span>
    </li>
    <li><span>Durée du compte:</span><span className='wrp-color-info'>{moment().to(accountdate)}</span></li>
   </ul>
  </nav>
 )
}
