import React from 'react';
import './Btn.css';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { doc, onSnapshot, collection, getDocs, deleteDoc } from "firebase/firestore";
import { db } from '../../firebase';
import { phoneX } from '../Phone/Input';

let pushArray = new Array();

// Return Btn Component
export default function ReturnBtn() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-btn-remove'>
  <VieWBtn />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-btn-remove-sm'>
  <VieWBtn />
 </div>
);
export const VieWBtn = () => (
 <>
  <BtnCancel />
  <BtnRemove />
 </>
);

export const BtnCancel = () => {

 const navigation = useNavigate();
 const handlepath = (event) => {
  event.preventDefault();
  window.localStorage.setItem('BUILD', JSON.stringify(false));
  navigation('/dash');
 }
 return (
  <button onClick={handlepath} className='Btn'>Annuler</button>
 );
}
export const BtnRemove = () => {

 const navigation = useNavigate();

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushArray.push(doc.id);
  });

 }, []);

 let phoneNum = window.localStorage.getItem('PHONE_USER_REMOVE');
 const checkNum = pushArray.some(value => value == phoneNum);

 const handlepath = async (event) => {

  event.preventDefault();
  window.console.log(checkNum);
  // navigation('/dash');
 }

 return (
  <button onClick={handlepath} className='Btn-Red'>Supprimer</button>
 );
};
