import React from 'react';
import { db } from '../../firebase';
import { doc, onSnapshot, collection, getDocs } from "firebase/firestore";
// import { phoneX } from '../../Phone/Input';
import './Swap.css';

let pushDocs = new Array();

// Swap Continue 
export default function ReturnSwAp() {

 const [toSwap, setToSwap] = React.useState([]);

 React.useEffect(async () => {

  const phoneUserTrafik = window.localStorage.getItem('PHONE_USER_TRAFIK');
  const querySnapshot = await getDocs(collection(db, "client"));

  querySnapshot.forEach((doc) => {
   // doc.data() is never undefined for query doc snapshots
   pushDocs.push(doc.id);
  });

  const verifier = pushDocs.some(value => value == JSON.parse(phoneUserTrafik));

  const unsub = onSnapshot(doc(db, verifier ? "client" : "agent", JSON.parse(phoneUserTrafik)), (doc) => {
   setToSwap(doc.data().swap);
  });

  window.console.log(toSwap[0]);

 }, []);

 return (
  <div className='wrp-swap-list'>
   {[...toSwap].map((value) => {

    return (
     <div className='wrp-swap-sub-list'>

      <h2>{value.type}</h2>
      <h2>{value.solde}</h2>
      <h2>{value.date}</h2>
      <h2>{value.phone}</h2>
      <h2>{value.user}</h2>

      {/* {Object.keys(value).map(function (key) {
       return (
        <p>{key}</p>
       )
      })} */}


     </div>
    )
   })}
  </div>
 );
};
