import React from 'react';
import './InputPhone.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';

// Firebase Auth for phone
import { signInWithPhoneNumber, RecaptchaVerifier } from "firebase/auth";
// import { auth } from '../firebase';

// Input Field form
export default function REturnInPutCode() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='form-input-sign'>
  <FormDataInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='form-input-sign-sm'>
  <FormDataInput />
 </div>
);
export const FormDataInput = () => {

 const [checked, setChecked] = React.useState(true);
 const { register, handleSubmit, formState, reset } = useForm({

  defaultValues: { phone: '+243' },
  mode: 'onChange'
 });

 const { isSubmitting, isValid, isSubmitSuccessful } = formState;
 const navigation = useNavigate();
 const onSubmit = async data => {

  if (data.phone.length != 13 && checked == true) {
   window.alert('Erreur numero entre!');
  } else {


   window.RecaptchaVerifier = new RecaptchaVerifier('sign-button',)


   setChecked(false)
  };
  checked === false && navigation('/dash')
  reset();
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   {checked ?
    <>
     <input type='phone' id='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
     <div className='recaptcha-container'></div>
     <button disabled={isSubmitting} className='Btn' >Suivant</button>
    </>
    :
    <>
     <input type='text' placeholder='Code Envoyer' name='code' {...register('code', { required: true })} />
     <button disabled={isSubmitting || !isValid} className='Btn' >Connexion</button>
    </>
   }
  </form>
 );
};
