import './Card.css';
import ReturnIconCard from './Icon';
// Icon and text view
import { IoIosPeople, IoIosPhonePortrait, IoIosWallet } from 'react-icons/io';
import Media from 'react-media';

// CaRd Main 
export default function ReturnCaRd() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-card-homed'>
  <div className='wrp-subcard-homed'>
   <ReturnIconCard icon={<IoIosPeople size={'2em'} />} Text={'Personnel'} />
   <ReturnIconCard icon={<IoIosPhonePortrait size={'2em'} />} Text={'Business'} />
   <ReturnIconCard icon={<IoIosWallet size={'2em'} />} Text={'Sécurisé'} />
  </div>
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-card-homed-sm'>
  <div className='wrp-subcard-homed-sm'>
   <ReturnIconCard icon={<IoIosPeople size={'1.4em'} />} Text={'Personnel'} />
   <ReturnIconCard icon={<IoIosPhonePortrait size={'1.4em'} />} Text={'Business'} />
   <ReturnIconCard icon={<IoIosWallet size={'1.4em'} />} Text={'Sécurisé'} />
  </div>
 </div>
);
