import Media from 'react-media';
import './Quote.css';
// Return Quote Component
export default function ReturnQuOte() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 )
};

export const ScreenSmall = () => (
 <div className='wrp-Qt-black-sm'>
  <ViewQuote />
 </div>
);
export const ScreenLarge = () => (
 <div className='wrp-Qt-black'>
  <ViewQuote />
 </div>
);
export const ViewQuote = () => (
 <p>
  Depuis l’avènement de la monnaie comme intermédiaire
  des échanges entre agents économiques, ses formes ont évolué.
 </p>
);
