import React from 'react';
import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Phone/Input';
import { doc, updateDoc, increment, onSnapshot, arrayUnion } from "firebase/firestore";

import { db } from '../../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';
import moment from 'moment';
import 'moment/locale/fr';

export let prixSuccess = 0;
export let nowField = moment().date();
export let now = (moment().date());

var regular = /[a-z]+/;
let arrayEmUsd = new Array();
let arrayAgentUsd = new Array();


// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 const navigation = useNavigate();
 const { handleSubmit, register, reset } = useForm();

 const [getPin, setGetPin] = React.useState('');
 const [first, setFirst] = React.useState('');
 const [last, setLast] = React.useState('');
 const [solde, setSolde] = React.useState(null);
 const [trade, setTrade] = React.useState(null);


 //  order notebook
 const [tableEmUsd, setTableEmUsd] = React.useState();
 const [tableAgentUsd, setTableAgentUsd] = React.useState();


 const [open, setOpen] = React.useState(false);
 const [fullWidth, setFullWidth] = React.useState(true);
 const [maxWidth, setMaxWidth] = React.useState('sm');


 const handleClickOpen = () => {
  setOpen(true);
 };
 const handleClose = () => {
  setOpen(false);
 };

 React.useEffect(async () => {

  try {

   await onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
    setGetPin(doc.data().code);
    setSolde(doc.data().usd);
   });
   await onSnapshot(doc(db, "agent", phoneX), (doc) => {

    setFirst(doc.data().firstname);
    setLast(doc.data().lastname);
    setTrade(doc.data().trade);

   });
   await onSnapshot(doc(db, "muungano", "emoney"), (doc) => {
    setTableEmUsd(doc.data().usd);
   });

   await onSnapshot(doc(db, "agents", "emoney"), (doc) => {
    setTableAgentUsd(doc.data().usd);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 const onSubmit = async (data) => {

  if (getPin == data.code) {

   if (data.count > 1000 || data.count <= 0 || data.count <= 1 || solde <= 1 || data.count > solde || regular.test(data.count)) {
    setOpen(true);
    reset();
   } else {

    let User = first + ' ' + last;
    sendAgentMoney(Math.floor(Number(data.count)), phoneX, User);

    updateAdmin(Number(data.count), Math.floor(Number(data.count)), User);
    const soldeEmUsd = Number(tableEmUsd[nowField]) + Number(data.count);
    const soldeAgentUsd = Number(tableAgentUsd[nowField]) + Number(data.count);


    arrayEmUsd = tableEmUsd;
    arrayAgentUsd = tableAgentUsd;

    for (let i = now; i <= 31; i++) {

     arrayEmUsd[i] = soldeEmUsd;
     arrayAgentUsd[i] = soldeAgentUsd;

    }

    updateMuungano(arrayEmUsd, arrayAgentUsd);
    prixSuccess = data.count;
    navigation('/send/success-usd');

   }
  } else {

   prixSuccess = data.count;
   setOpen(true);
   reset();
  }

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type={'text'}
    {...register('count', { required: true })}
    name="count"
    placeholder={'USD'}
   />
   <input type={'password'}
    {...register('code', { required: true })}
    name="code"
    placeholder={'••••••'}
   />

   <button className='Btn'>Envoyer</button>

   <Dialog
    fullWidth={fullWidth}
    maxWidth={maxWidth}
    open={open}
    onClose={handleClose}
   >
    <DialogTitle>MuunganoMoney</DialogTitle>
    <DialogContent>

     <DialogContentText>
      Le solde entre n'est pas valide, veuillez vérifier, Ou code d'accès erroné
     </DialogContentText>

    </DialogContent>
    <DialogActions>
     <Button onClick={handleClose}>Fermer</Button>
    </DialogActions>
   </Dialog>

  </form>
 );
};

async function sendAgentMoney(solde, numPhone) {

 let time = moment().format('LLL');
 let send = { date: time, solde: `${solde} USD`, phone: numPhone, user: 'muunganomoney', type: 'réussi' }

 const washingtonRef = doc(db, "agent", numPhone);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: increment(solde),
  money: solde,
  unite: 'USD',
  user: 'muunganomoney',
  delay: moment().format(),
  swap: arrayUnion(send)
 });

};
async function updateAdmin(solde, money, User) {

 const washingtonRef = doc(db, "muunganomoney", "money");
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: increment(-(solde)),
  money: money,
  buyusd: solde,
  unite: 'USD',
  user: User,
  delay: moment().format()
 });

 const emoneyRef = doc(db, "muunganomoney", "e-money");
 await updateDoc(emoneyRef, {
  cdf: solde
 });

 const moneyRef = doc(db, "muunganomoney", "money");
 await updateDoc(moneyRef, {
  cdf: increment(-(solde))
 });

};
async function updateMuungano(emUsd, agentUsd) {

 const emoneyRef = doc(db, "muungano", "emoney");
 await updateDoc(emoneyRef, {
  usd: emUsd
 });

 const agentsemoneyRef = doc(db, "agents", "emoney");
 await updateDoc(agentsemoneyRef, {
  usd: agentUsd
 });

};
