import React from 'react';
import './Icon.css';
import { VscCheck } from 'react-icons/vsc';
import { TweenMax, Expo } from 'gsap';
import Media from 'react-media';

// Return Icon
export default function ReturnICon() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => {
 return (
  <div className='wrp-success-icon'>
   <ViewIcon />
  </div>
 );
}
export const ScreenSmall = () => {
 return (
  <div className='wrp-success-icon-sm'>
   <ViewIcon />
  </div>
 );
};
export const ViewIcon = () => {

 React.useEffect(() => {
  TweenMax.from('.Anime', 1.5, { delay: 3, opacity: 0, ease: Expo.easeIn });
  TweenMax.from('.Anime h1', 1.5, { delay: 3.9, opacity: 0, y: 20, ease: Expo.easeIn });
 }, [])

 return (
  <div className='Anime'>
   <VscCheck size={'2em'} />
   <h1>Envoyer avec success</h1>
  </div>
 )
}
