import React from 'react';
import Media from 'react-media';
import './Quote.css';
import { TweenMax, Expo } from 'gsap';

// Return Quote Component
export default function ReturnQuOte() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-Qt-add'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-Qt-add-sm'>
  <ViewText />
 </div>
);
export const ViewText = () => {

 React.useEffect(() => {
  TweenMax.from('.wrp-qt-trans-add', 1.2, { delay: 0.5, opacity: 0, y: 20, ease: Expo.easeIn });

 }, []);

 return (
  <p className='wrp-qt-trans-add'>
   Nous pouvons considérer la monnaie électronique comme une réserve
   électronique comme valeur monétaire.
  </p>
 );
};
