import React from 'react';
import './Exch.css';
import Media from 'react-media';
import { db } from '../../firebase';
import { doc, onSnapshot } from "firebase/firestore";

// Exch Date Money
export default function ReturnExChdAsh() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};


export const ScreenLarge = () => (
 <div className='wrp-exch-dash'>
  <Exch />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-exch-dash'>
  <Exch />
 </div>
);
export const Exch = () => {

 const [prix, setPrix] = React.useState(null);
 const [unity, setUnity] = React.useState('');

 React.useEffect(async () => {
  await onSnapshot(doc(db, "muunganomoney", "money"), (doc) => {
   setPrix(doc.data().money);
   setUnity(doc.data().unite);
  });

 }, []);

 return (
  <>
   <h2>Money</h2>
   <p><span>{prix}</span><span>{unity.toUpperCase()}</span></p>
  </>
 );
};
