import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { db } from '../../firebase';

export let prixSuccess = 0;

// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 const navigation = useNavigate();
 const { handleSubmit, register, reset } = useForm();

 const onSubmit = async (data) => {

  let getPin = '';
  let soldeCdf = 0;
  let soldeUsd = 0;

  const docRefPin = doc(db, "ungama", "money");
  const docSnapPin = await getDoc(docRefPin);

  if (docSnapPin.exists()) {
   getPin = docSnapPin.data().pin;
   soldeCdf = docSnapPin.data().fc;
   soldeUsd = docSnapPin.data().usd;
  }

  soldeCdf = soldeCdf + data.fran;
  soldeUsd = soldeUsd + data.dollar;

  if (getPin == data.code && data.code.length == 6) {

   updateSolde(data.dollar, data.fran);
   window.alert(`La quantiter à etait augementer avec success.`);
   navigation('/dash');

  } else {
   window.alert(`Erreur de code d'accès invalide`)
  }

  reset();
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type={'text'}
    {...register('dollar', { required: true })}
    name="dollar" placeholder={'$'} />

   <input type={'text'}
    {...register('fran', { required: true })}
    name="fran" placeholder={'CDF'} />

   <input type={'text'}
    {...register('code', { required: true })}
    name="code"
    placeholder={'••••••'}
   />

   <button className='Btn'>Envoyer</button>
  </form>
 );
};

