import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { doc, setDoc, updateDoc, increment, onSnapshot } from 'firebase/firestore';
import { db } from '../../firebase';
import { phoneX } from '../Phone/Input';

import dayjs from 'dayjs';

// import Stack from '@mui/material/Stack';
// import TextField from '@mui/material/TextField';
// import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
// import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
// import { TimePicker } from '@mui/x-date-pickers/TimePicker';
// import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
// import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
// import { MobileDatePicker } from '@mui/x-date-pickers/MobileDatePicker';


import moment from 'moment';
import 'moment/locale/fr';
export let nowField = (moment().date()) - 1;


// Input Field Component 
export default function ReturnInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <SreenLarge />}
     {matches.large && <SreenLarge />}
    </>
   )}
  </Media>
 );
};

export const SreenLarge = () => {
 return (
  <div className='wrp-input-signin-register'>
   <FormInput />

  </div>
 )
}
export const ScreenSmall = () => {
 return (
  <div className='wrp-input-signin-register-sm'>
   <FormInput />

  </div>
 )
};
export const FormInput = () => {

 const [value, setValue] = React.useState(dayjs('2000-08-18T21:11:54'));
 const [tablepartner, setTablepartner] = React.useState();

 const handleChange = (newValue) => {
  setValue(newValue);
 };

 let regular = /[a-z]+/;
 const navigation = useNavigate();
 const existing = { state: "client", cdf: 0, usd: 0, thriftcdf: 0, thriftusd: 0, accountdate: moment().format(), delay: '', code: 'muuganomoney', phoneclient: '', unite: '', solde: 0, money: 0, frais: 0, user: '' }
 const { register, handleSubmit } = useForm({
  defaultValues: {
   phone: '+243'
  }
 });

 React.useEffect(async () => {

  try {
   await onSnapshot(doc(db, "muungano", "partner"), (doc) => {
    setTablepartner(doc.data().client);
   });

  } catch {
   window.console.log('erreur disponible');
  }

 }, []);

 // Ref collection database!
 const onSubmit = async (data) => {

  if (data.phone.length != 13 || regular.test(data.phone)) {
   window.alert('Numéro de téléphone est Incorrect!');
  } else {

   let valuePartner = Number(tablepartner[nowField]) + 1;
   let arrayPartner = tablepartner;
   arrayPartner[nowField] = valuePartner;

   const clone = Object.assign(data, existing);
   saveInfoInWithDocs(phoneX, clone, arrayPartner);
   navigation('/register/success');
  }
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type='text' placeholder='Nom' name='firstname' {...register('firstname', { required: true })} />
   <input type='text' placeholder='Prènom' name='lastname' {...register('lastname', { required: true })} />
   <input type='email' placeholder='Email' name='email' {...register('email', { required: false })} />
   <input type='phone' placeholder='Phone' name='phone' {...register('phone', { required: true })} />
   {/* <input type='text' placeholder='Nom du Business' name='job' {...register('job', { required: true })} /> */}
   <input type='text' placeholder='Ville' name='ville' {...register('ville', { required: true })} />
   <input type='text' placeholder='Addresse' name='addresse' {...register('addresse', { required: true })} />
   <input type='date' placeholder='date de naissance' name='birth' {...register('birth', { required: true })} />


   {/* <LocalizationProvider dateAdapter={AdapterDayjs}>
    <Stack spacing={3}>

     <DesktopDatePicker
      label="Date de naissance"
      inputFormat="MM/DD/YYYY"
      value={value}
      onChange={handleChange}
      name='birth'
      {...register('birth', { required: true })}
      renderInput={(params) => <TextField {...params} />}
     />
    </Stack>
   </LocalizationProvider> */}


   <button className='Btn'>Enregistre</button>
  </form>
 )
};

async function saveInfoInWithDocs(phoneInDocs, user, arrayPartner) {
 await setDoc(doc(db, "client", phoneInDocs), user);

 const washingtonRef = doc(db, "muunganomoney", "partner");
 await updateDoc(washingtonRef, {
  client: increment(1)
 });

 const partnerRef = doc(db, "muungano", "partner");
 await updateDoc(partnerRef, {
  client: arrayPartner,
 });

};
