import React from 'react';
import './Prix.css';
import Media from 'react-media';
import { Fc, Usd, Blocked } from '../Money';
import { doc, collection, getDocs, onSnapshot } from "firebase/firestore";
import { db } from '../firebase';
import { phoneX } from '../Sign/Input';


// REturn view Prix available
export default function REturnPriX() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='current-withdraw'>
  <WithdrawBalance />
 </div>
);
export const ScreenSmall = () => (
 <div className='current-withdraw-sm'>
  <WithdrawBalance />
 </div>
);
export const WithdrawBalance = () => {

 let arrayClient = new Array();
 const [fc, setFc] = React.useState(Fc);
 const [usd, setUsd] = React.useState(Usd);
 const [blocked, setBlocked] = React.useState(Blocked);

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   arrayClient.push(doc.id);
  });
  const boolCollect = arrayClient.some((value) => value == phoneX);
  const unsub = onSnapshot(doc(db, boolCollect ? "client" : "agent", phoneX), (doc) => {
   setFc(doc.data().cdf);
   setUsd(doc.data().usd);
   setBlocked(doc.data().thriftcdf);

  });

 }, []);

 return (
  <p>
   <span>{usd}USD</span>
   <span>{fc}CDF</span>
   <span className='wrp-grey-view'>{blocked}CDF</span>
  </p>
 )
};
