import './Input.css';
import { useForm } from 'react-hook-form';
import Media from 'react-media';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Phone/Input';
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { db } from '../../firebase';


export let prixSuccess = '';

// withdraw input
export default function ReturnWithdrAwInput() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-withdraw-input'>
  <FormInput />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-withdraw-input-sm'>
  <FormInput />
 </div>
);
export const FormInput = () => {

 const navigation = useNavigate();
 const { handleSubmit, register, reset } = useForm();

 const onSubmit = async (data) => {

  let getPin = '';

  const docRefPin = doc(db, "ungama", "money");
  const docSnapPin = await getDoc(docRefPin);

  if (docSnapPin.exists()) {
   getPin = docSnapPin.data().pin;
  }

  if (getPin == data.code && data.code.length == 6) {

   addSoldeAgent(phoneX, data.count);
   updateSolde(data.count);
   prixSuccess = data.count;
   navigation('/send/success-usd');

  } else {
   window.alert(`Erreur de code d'accès invalide`)
   reset();
  }
  reset();
 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>

   <input type={'text'}
    {...register('count', { required: true })}
    name="count"
    placeholder={'$'}
   />
   <input type={'text'}
    {...register('code', { required: true })}
    name="code"
    placeholder={'••••••'}
   />

   <button className='Btn'>Envoyer</button>
  </form>
 );
};

// get money pyrami
export const updateSolde = async (solde) => {

 let getUsd = 0;
 let newSolde = 0;
 let actif = 0;

 const docRef = doc(db, "ungama", 'money');
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  getUsd = docSnap.data().usd;
  actif = docSnap.data().allusd;
 }

 newSolde = Number(getUsd) - Number(solde);
 actif = Number(actif) + Number(solde);

 const washingtonRef = doc(db, "ungama", "money");

 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: Math.floor(newSolde),
  allusd: Math.floor(actif),
 });

};
// get add some money on the agent
export const addSoldeAgent = async (numPhone, solde) => {

 let getUsd = 0;

 const docRef = doc(db, "agent", numPhone);
 const docSnap = await getDoc(docRef);

 if (docSnap.exists()) {
  getUsd = Number(docSnap.data().usd) + Number(solde);
 };

 const washingtonRef = doc(db, "agent", numPhone);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, {
  usd: Math.floor(getUsd),
  money: Math.floor(solde),
  unite: 'usd'
 });
};
