import React from 'react';
import './Input.css';
import Media from 'react-media';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { phoneX } from '../Sign/Input';

import { collection, getDocs, doc, updateDoc } from 'firebase/firestore';
import { db } from '../firebase';

import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContentText from '@mui/material/DialogContentText';



// View Form Update view
export default function ReturnFormUpdate() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-form-input-update'>
  <FormInputField />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-form-input-update-sm'>
  <FormInputField />
 </div>
);
export const FormInputField = () => {

 let pushDocs = new Array();
 const navigation = useNavigate();
 const { register, handleSubmit, reset } = useForm();

 React.useEffect(async () => {

  const querySnapshot = await getDocs(collection(db, "client"));
  querySnapshot.forEach((doc) => {
   pushDocs.push(doc.id);
  });

 }, []);

 const onSubmit = async (data) => {

  if (data.code.length != 6) {

   window.alert('6 lettres accepter.');
   reset();

  } else {

   const verifierCollection = pushDocs.some((value) => value == phoneX);
   updatePinInWithDocs(verifierCollection, data.code, phoneX);
   navigation('/dash');
  };

 };

 return (
  <form onSubmit={handleSubmit(onSubmit)}>
   <input type={'text'} placeholder='••••••' {...register('code')} />
   <button className='Btn'>Enregistre</button>
  </form>
 );
};

async function updatePinInWithDocs(verifierCollection, newPin, numPhone) {
 const washingtonRef = doc(db, verifierCollection ? "client" : "agent", numPhone);
 // Set the "capital" field of the city 'DC'
 await updateDoc(washingtonRef, { code: newPin });
};
