import React from 'react';
import './Count.css';
import Media from 'react-media';

let phoneX = 0;

// Return view Name
export default function RetuRnCount() {
 return (
  <Media
   queries={{
    small: '(max-width: 599px)',
    medium: '(min-width: 600px) and (max-width:1199px)',
    large: '(min-width: 1200px)',
   }}>
   {matches => (
    <>
     {matches.small && <ScreenSmall />}
     {matches.medium && <ScreenLarge />}
     {matches.large && <ScreenLarge />}
    </>
   )}
  </Media>
 );
};

export const ScreenLarge = () => (
 <div className='wrp-name-insolde'>
  <ViewText />
 </div>
);
export const ScreenSmall = () => (
 <div className='wrp-name-insolde-sm'>
  <ViewText />
 </div>
);

export const ViewText = () => {
 const [solde, setSolde] = React.useState('230');
 const [unity, setUnity] = React.useState('fc');

 React.useEffect(async () => {

  const docRef = doc(db, "agent", phoneX);
  const docSnap = await getDoc(docRef);

  if (docSnap.exists()) {
   setSolde(docSnap.data().newsolde)
   setUnity(docSnap.data().unity)
  } else {
   // doc.data() will be undefined in this case
   console.log("No such document!");
  }

 }, []);

 return (
  <h2>{solde}{unity}</h2>
 );
};
